function() {
  if(app.buildProject.type === "bliss") {
    app.buildProject.build = "designer";
  }

  if(typeof app.buildProject.js.createState === "undefined") {
		app.buildProject.js.createState = app.js.createState.toString();
  }

  if(typeof app.buildProject.js.getState === "undefined") {
    app.buildProject.js.getState = app.js.getState.toString();
  }

  var data = JSON.stringify(app.buildProject);

  $.ajax({
    type: 'POST',
    url: '/build',
    data: data,
    success: function(data) {
      var iframe = $('#preview');
      var url = location.origin + '/designer/designer.html';
      if(iframe.attr('src') !== url) {
        iframe.attr('src', url);
      } else {
        try {
          iframe[0].contentWindow.location.reload();
        } catch(e){
          console.log(e);
        }
      }
    },
    contentType: "application/json",
    dataType: 'json'
  });
}

