    "type": "designer",
    "build": "designer",
    "compiler": "react",
    "next_id": 2,
    "root_id": "1",
    "externalCss": [],
    "externalJs": [
      "node_modules/react/dist/react.js",
      "node_modules/react-dom/dist/react-dom.js",
    ],
    "state": {},
    "js": {
      "init": "function() { app.render(); }"
    },
    "load": ["init"],
    "components": {
      "1": {
        "id": "1",
