function(scope, attributes) {
  var comp = this;

  return function(e) {
    var ENTER = 13, ESCAPE = 27, key = e.which;
    if(key !== ENTER && key !== ESCAPE) return;

    if(key === ENTER && app.state.showProjectInputType === "new") {
      comp.newProject(app.state.projectInputValue);
    } else if(key === ENTER && app.state.showProjectInputType === "load") {
      comp.loadProject(app.state.projectInputValue);
    } else if(key === ENTER && app.state.showProjectInputType === "save as") {
      comp.saveAsProject(app.state.projectInputValue);
    }

    app.setState(function() {
      app.state.showProjectInput = false;
      app.state.showProjectInputType = "";
      app.state.projectInputValue = "";
    });
  }
};

