function() {
  app.setState(function() {
    // Configure Project
    app.buildProject = newBlissProject;

    // Set State
    app.state.showContent = 'designer';
    app.state.activeComponent = app.buildProject.rootId;

    var color = state.create('color');
    color.setData('currentColor', '#ffffff');

    var res = state.create('res');
    res.create({value: 'full', label: 'Full width', width: '100%' });
    res.create({value: 'galaxys5', label: 'Galaxy S5', width: '360px', height: '640px'});
    res.create({value: 'nexus5x', label: 'Nexus 5X', width: '412px', height: '732px'});
    res.create({value: 'nexus6p', label: 'Nexus 6P', width: '412px', height: '732px'});
    res.create({value: 'iphone5', label: 'iPhone 5', width: '320px', height: '568px'});
    res.create({value: 'iphone6', label: 'iPhone 6', width: '375px', height: '667px'});
    res.create({value: 'iphone6plus', label: 'iPhone 6 Plus', width: '414px', height: '736px'});
    res.create({value: 'ipad', label: 'iPad', width: '768px', height: '1024px'});
    res.create({value: 'ipadpro', label: 'iPad Pro', width: '1024px', height: '1366px'});
    res.setData('selected', 'full');

    var display = state.create('display');
    display.create({name: 'components', width: '20%', active: true});
    display.create({name: 'designer', width: '60%', width2: '80%', width3: '100%', active: true});
    display.create({name: 'properties', width: '20%', active: true});

    var projects = state.create('projects');
    app.js.getProjects();

    app.js.setupNewProject();
  });
}

function() {
  if(app.buildProject.type === "bliss") {
    app.buildProject.build = "designer";
  }

  var data = JSON.stringify(app.buildProject);

  $.ajax({
    type: 'POST',
    url: '/build',
    data: data,
    success: function(data) {
      var iframe = $('#preview');
      var url = location.origin + '/designer/designer.html';
      if(iframe.attr('src') !== url) {
        iframe.attr('src', url);
      } else {
        try {
          iframe[0].contentWindow.location.reload();
        } catch(e){
          console.log(e);
        }
      }
    },
    contentType: "application/json",
    dataType: 'json'
  });
}

