function() {
  app._state = state();
  app.js.cleanState(newBlissProject, false);
}

function() {
  app.js.log('app.js.build() invoked.');
  if(app.buildProject.type === "bliss") {
    app.buildProject.build = "designer";
  }

  var data = JSON.stringify(app.buildProject);

  $.ajax({
    type: 'POST',
    url: '/compiler/build',
    data: data,
    success: function(data) {
      app.js.refreshIframe();
    },
    error: function(jqXHR, textStatus, errorThrown) {
      console.error('POST /build', jqXHR, textStatus, errorThrown);
    },
    contentType: "application/json",
    dataType: 'json'
  });
}

function(id) {
  app.js.log('app.js.selectComponent() invoked.');
  app.setState(function() {
    var internal = app._state.get('internal');
  	internal.setData('activeComponent', id);
  });
}

function(fn) {
  app.js.log('app.js.update() invoked.');
  app.setState(function() {
    app.state.shouldSave = true;
    clearTimeout(app.state.timer);
    try { fn(); } catch(e) { console.error('app.js.update', e); }
    app.state.timer = setTimeout(function() {
    	if(app.state.shouldBuild === true) {
        app.js.build();
      } else {
        app.js.saveAndReloadProject();
      }
  	}, 500);
  });
}

function(path, success, data, requestType) {
  app.js.log('app.js.server() invoked.');
  if(_.isNil(data)) data = {};
  if(_.isNil(requestType)) requestType = 'GET';

  $.ajax({
    type: requestType,
    url: '/project/' + path,
    data: data,
    success: function(data) {
      success(data);
    },
    contentType: "application/json",
    dataType: 'json'
  });
}

function(scope, attributes) {
  app.js.log('app.js.getProjects() invoked.');
  var projects = app._state.get('projects');
  app.js.server('list', function(data) {
    projects.removeAll();
    app.setState(function() {
      data.projects.forEach(function(project) {
        projects.create({name: project});
      });
    });
  });
}

function(projectName, shouldConfirm) {
  app.js.log('app.js.loadProjects() invoked.');
  if(_.isNil(shouldConfirm)) shouldConfirm = true;
  if(shouldConfirm === true) {
  	if(!confirm('Are you sure you want to load a different project?')) return;
  }

  app.setState(function() {
  	app.js.setStatus('Loading project ' + projectName + '...');
  });

  app.js.server('load', function(data) {
    app.setState(function() {
      app.js.setStatus('Loaded project ' + data.project.name + '.');
    });
    app.js.cleanState(data.project, true);
    app.js.build();
  }, {name: projectName});
}

function(message) {
  app.js.log('app.js.setStatus() invoked.');
  app.setState(function() {
  	app.state.status = message;
  });
}

function(dollarName) {
  var name = dollarName.substring(1);
  var found = _.find(blissProject.cssVars, {'name': name});
  return (_.isNil(found)) ? '' : found.value;
}

function(success) {
  app.js.log('app.js.saveProject() invoked.');
  var proj = app.buildProject;
  var data = JSON.stringify(proj);
  app.js.setStatus('Saving project ' + proj.name + '...');

  $.ajax({
    type: 'POST',
    url: '/project/save',
    data: data,
    success: function(data) {
      app.js.setStatus('Saved project ' + proj.name + '.');
      if(!_.isNil(success)) success(data);
    },
    contentType: "application/json",
    dataType: 'json'
  });
}

function(shouldConfirm) {
  app.js.log('app.js.createNewProject() invoked.');
  if(_.isNil(shouldConfirm)) shouldConfirm = true;

  if(shouldConfirm === true) {
    if(!confirm('Are you sure you want to create a new project?')) return;
  }

  app.setState(function() {
    app.buildProject = newBlissProject;
		app.state.shouldBuild = false;
  });
}

function() {
  app.js.log('app.js.saveAndReloadProject() invoked.');
  // save current json
  app.js.saveProject(function() {
    // load project
    app.js.loadProject(app.buildProject.name, false);
  });
}

function() {
  app.js.log('app.js.refreshIframe() invoked.');
  var iframe = $('#preview');
  var currentSrc = iframe.attr('src');
  if(_.isUndefined(currentSrc)) return;

  var url = location.origin + '/bliss/designer/designer.html';
  iframe.attr('src', url);
}

function() {
  app.js.log('app.js.refresh() invoked.');
  // refresh the project list
  app.js.getProjects();

  // refresh iframe
  app.js.refreshIframe();
}

function(buildProject, shouldBuildProject) {
  app.js.log('app.js.cleanState() invoked.');
  app.setState(function() {
    // Clean existing state
    //app._state.reset();
    app._state = state();

    // Set build project
    app.js.log('setting build', buildProject);
    app.buildProject = buildProject;

    // Set State
    app.state.shouldSave = false;
    app.state.shouldBuild = shouldBuildProject;

    // Set internal state
    var internal = app._state.create('internal');
    internal.setData('activeComponent', app.buildProject.rootId);

    var view = app._state.create('view');
    view.create({name: 'designer', label: 'Designer'});
    view.create({name: 'js', label: 'JavaScript'});
    view.create({name: 'global_js', label: 'Global JS'});
    view.create({name: 'global_css', label: 'Global CSS'});
    view.create({name: 'css_vars', label: 'CSS Variables'});
    view.create({name: 'page_load', label: 'Page Load'});
    view.create({name: 'node_packages', label: 'Node Packages'});
    view.create({name: 'settings', label: 'Settings'});
    view.setData('selected', 'designer');

    var color = app._state.create('color');
    color.setData('currentColor', '#ffffff');

    var res = app._state.create('res');
    res.create({value: 'full', label: 'Viewport', width: '100%', height: 'calc(100% - 32px)', previewWidth: '100%', previewHeight: 'calc(100vh - 100px)'});
    res.create({value: 'galaxys5', label: 'Galaxy S5', width: '360px', height: '640px', previewWidth: 'calc(360px + 20px)', previewHeight: 'calc(640px + 52px)'});
    res.create({value: 'nexus5x', label: 'Nexus 5X', width: '412px', height: '732px', previewWidth: 'calc(412px + 20px)', previewHeight: 'calc(732px + 52px)'});
    res.create({value: 'nexus6p', label: 'Nexus 6P', width: '412px', height: '732px', previewWidth: 'calc(412px + 20px)', previewHeight: 'calc(732px + 52px)'});
    res.create({value: 'iphone5', label: 'iPhone 5', width: '320px', height: '568px', previewWidth: 'calc(320px + 20px)', previewHeight: 'calc(568px + 52px)'});
    res.create({value: 'iphone6', label: 'iPhone 6', width: '375px', height: '667px', previewWidth: 'calc(375px + 20px)', previewHeight: 'calc(667px + 52px)'});
    res.create({value: 'iphone6plus', label: 'iPhone 6 Plus', width: '414px', height: '736px', previewWidth: 'calc(414px + 20px)', previewHeight: 'calc(736px + 52px)'});
    res.create({value: 'ipad', label: 'iPad', width: '768px', height: '1024px', previewWidth: 'calc(768px + 20px)', previewHeight: 'calc(1024px + 52px)'});
    res.create({value: 'ipadpro', label: 'iPad Pro', width: '1024px', height: '1366px', previewWidth: 'calc(1024px + 20px)', previewHeight: 'calc(1366px + 52px)'});
    res.setData('selected', 'full');

    var display = app._state.create('display');
    display.create({name: 'components', width: '20%', active: true});
    display.create({name: 'designer', width: '60%', width2: '80%', width3: '100%', active: true});
    display.create({name: 'properties', width: '20%', active: true});

    var projects = app._state.create('projects');
    app.js.getProjects();
  });
}

